var THREE = require("three");
var Orbitcontrols = require("three-orbitcontrols");

var width = window.innerWidth;
var height = window.innerHeight;

// 创建场景
var scene = new THREE.Scene();

// 创建远景相机
var camera = new THREE.PerspectiveCamera(75, width/height, 0.1, 800);
// 修改相机的位置，避免重叠
camera.position.set(0, 0, 100);

// 将照相机的视角对准舞台
camera.lookAt(scene.position);

// 创建渲染器
var renderer = new THREE.WebGLRenderer();
// 渲染大小
renderer.setSize(width, height);
// 将画布添加到body中
document.body.appendChild(renderer.domElement);


// 摄像机插件 控制摄像机的缩放。
var controls = new Orbitcontrols(camera, renderer.domElement);
// 启动摄像机缩放
controls.enableZoom = true;

// 搭建模型
var geometry = new THREE.Geometry(); 
// 加载纹理(图片)
var texture = new THREE.TextureLoader().load("./images/snow.jpg"); 
// 控制图片
var material = new THREE.PointsMaterial({
    size: 20, // 大小
    map: texture,
    transparent: true,
    depthWrite: false,
    blending: THREE.AdditiveBlending
})
//创建更多的几何体（来承载许多雪花） 创建雪花， 雪花出现的位置
var range = 500;
for (var i = 0; i < 400; i++) {
    var particle = new THREE.Vector3(
            Math.random() * range - range / 2,
            Math.random() * range * 1.5,
            Math.random() * range - range / 2);
        particle.velocityY = 0.1 + Math.random() / 5;
        particle.velocityX = (Math.random() - 0.5) / 3;
        particle.velocityZ = (Math.random() - 0.5) / 3;
        geometry.vertices.push(particle);
}

// 模型和图片合体
var points = new THREE.Points(geometry, material);
// 添加到场景中
scene.add(points);



// 渲染
function render(){
    //每朵雪花移动的轨迹。
    scene.children.forEach(function (child) {
        if(child instanceof THREE.Points) {
            var vertices = child.geometry.vertices;
            child.geometry.verticesNeedUpdate = true;

            vertices.forEach(function (v) {
                v.y = v.y - (v.velocityY);
                v.x = v.x - (v.velocityX);
                v.z = v.z - (v.velocityZ);

                // y 的边界值
                if(v.y <= -height / 2) {
                    v.y = height / 2;
                }

                // x 的边界值
                if(v.x <= -width / 2 || v.x >= width / 2) {
                    v.velocityX = v.velocityX * -1;
                }

                // z 的边界值
                if(v.z <= -60 || v.z >= 60) {
                    v.velocityZ = v.velocityZ * -1;
                }
            })
        }
    })
    // 渲染成图形
    renderer.render(scene, camera);
    // 循环
    requestAnimationFrame(render); 
}
// start
render();
